package fme_test

import (
	"encoding/base64"
	"fmt"
	fme "github.com/gpioblink/go-auto-clean-fme-editor/fme/domain"
	"github.com/stretchr/testify/assert"
	"testing"
)

func TestNewFmeFromBinary(t *testing.T) {
	fmeData := decodeTestBytes()
	fmeStructData, err := fme.NewFmeFromBinary(fmeData)
	assert.NoError(t, err)

	fmeOut, err := fmeStructData.ExportBinary()
	assert.NoError(t, err)
	fmt.Printf("%#v", fmeStructData)
	assert.EqualValues(t, fmeData, fmeOut)
}

func decodeTestBytes() []byte {
	kimigayoBase64 := ""
	kimigayoBytes, _ := base64.StdEncoding.DecodeString(kimigayoBase64)
	return kimigayoBytes
}
